function generateCard(e){for(console.log(e),i=0;i<e.length;i++){let t=document.getElementById("work_container"),n=document.createElement("a");n.href=`./${e[i].Title}`,t.appendChild(n);let a=document.createElement("div");a.className="use_case",n.appendChild(a);let l=document.createElement("img");l.id="use_img";try{console.log(`${i} : ${e[i].Image.formats.thumbnail.url}`),l.src="http://127.0.0.1:1337"+e[i].Image.formats.thumbnail.url,a.appendChild(l)}catch(e){console.error(e+" "+i)}let o=document.createElement("div");o.id="use_text",o.innerHTML=`<span id="use_title">\n                                ${e[i].Title}\n                            </span>\n                            <br><br>\n                            <span id="use_blurb">\n                                ${e[i].Description}\n                            </span>`,a.appendChild(o)}}fetch("http://localhost:1337/posts",{method:"GET",headers:{"Content-Type":"application/json"}}).then(e=>e.json()).then(e=>generateCard(e));